import axios from 'axios';
import React from 'react';

const baseURL = "http://52.10.102.239/tickets/";

const ressource = axios.get(baseURL);

class PostTicket extends React.Component {
  state = {
    tickets: []
  }

  componentDidMount() {
    axios.get(baseURL)
      .then(res => {
        const tickets = res.data;
        this.setState({ tickets });
      })
  }

  render() {
    return (
      this.state.tickets
            .map(ticket =>
              <React.Fragment>
                <tr>
                  <td>{ticket.id}</td>
                  <td>{ticket.email}</td>
                  <td>{ticket.title}</td>
                  <td>{ticket.description}</td>
                  <td>
                    <div className="row">
                      <div className="col-6 text-center">
                        <button type="button" className="btn btn-sm btn-success">Valider</button>
                      </div>
                      <div className="col-6 text-center">
                        <button type="button" onClick={deleteTicket} className="btn btn-sm btn-danger">Supprimer</button>
                      </div>
                    </div>
                  </td>
                </tr>
              </React.Fragment>
            )
      
    )
  }
}

export default PostTicket;
