import axios from 'axios';
import React from 'react';

const baseURL = "http://52.10.102.239/tickets/";

const ressource = axios.get(baseURL);

class App extends React.Component {
  state = {
    tickets: []
  }

  componentDidMount() {
    axios.get(baseURL)
      .then(res => {
        const tickets = res.data;
        this.setState({ tickets });
      })
  }

  render() {
    return (
      this.state.tickets
            .map(ticket =>
              <React.Fragment>
                <tr>
                  <td>{ticket.id}</td>
                  <td>{ticket.email}</td>
                  <td>{ticket.title}</td>
                  <td>{ticket.description}</td>
                  <td>
                    <button type="button" className="btn btn-lg btn-block btn-primary create-ticket">Submit</button>
                    <button type="button" className="btn btn-lg btn-warning btn-block">Reset</button>
                  </td>
                </tr>
              </React.Fragment>
            )
      
    )
  }
}

export default App;
